mapper.insert:

	select product_seq.nextval from dual

insert into product(pnum, upcg_code, downcg_code, pname, pimage1, pimage2, pimage3,
price, saleprice, pqty, point, pspec, pcontents, pcompany, pindate)
values(#{pnum}, #{upCg_code}, #{downCg_code}, #{pname}
, #{pimage1:VARCHAR}, #{pimage2:VARCHAR}, #{pimage3:VARCHAR}
, #{price}, #{saleprice}, #{pqty}, #{point}, #{pspec}
, #{pcontents}, #{pcompany}, sysdate)
